targets.expr:
(sum(
        sum by (hostname) (avg_over_time(power_consumption_per_cpu_watts[$__range:]))
        * 
        sum by (hostname) (
            last_over_time(
                (
                    slurm_job_allocated_cores{jobid=~"$job_id",jobstate="RUNNING"} 
                    * on (jobid) group_right(hostname) 
                    slurm_job_runtime{jobid=~"$job_id",jobstate="RUNNING"}
                )
                [$__range:]))
) / 3600
+
(sum(
        sum by (hostname) (avg_over_time(power_consumption_per_gpu_watts[$__range:]))
        * 
        sum by (hostname) (
            last_over_time(
                (
                    slurm_job_allocated_gres{jobid=~"$job_id",jobstate="RUNNING",gres_type="gpu"} 
                    * on (jobid) group_right(hostname) 
                    slurm_job_runtime{jobid=~"$job_id",jobstate="RUNNING"}
                )
                [$__range:]))
) OR vector(0)) / 3600) * max(slurm_job_info{jobid=~"$job_id",jobstate="RUNNING"})

